mapper.select:

        select *
        from(
            select *
			from(
				select *
				from settingRecord
				order by timeset desc, setNum desc
			)
		where rownum<=14
        ) 
        where reason='admin'
        order by hwCode

		select *
		from (
			select *
            from SETTINGRECORD
			where TO_CHAR(setDate, 'YYYY/MM/DD HH24:MI:SS') = #{setDate}
			order by setNum desc		
			)
		where rownum<=14
		order by hwCode

	select *
		from (
			select *
            from SETTINGRECORD
			where TO_CHAR(TIMESET, 'YYYY/MM/DD HH24:MI') = #{timeSet}
			order by timeset desc, setNum desc
		)
		where rownum<=14
		order by hwCode
